mapper.update:
UPDATE auction_record
topic_item_id=#{topicItemId}, record_type=#{recordType}, deposit_type=#{depositType}, user_id=#{userId}, nickname=#{nickname}, mobile=#{mobile}, bid_num=#{bidNum}, exchange_count=#{exchangeCount}, refund_num=#{refundNum}, total_currency=#{totalCurrency}, current_auct_price=#{currentAuctPrice}, current_auct_count=#{currentAuctCount}, deposit_id=#{depositId}, card_no=#{cardNo}, deposit_amount=#{depositAmount}, discount_id=#{discountId}, discount=#{discount},
WHERE id = #{id}

mapper.select:
SELECT
FROM auction_record
ORDER BY auction_record.id DESC Limit #{start},#{pageSize}

SELECT
FROM auction_record ar WHERE ar.record_type = '01' AND ar.topic_item_id IN
#{tpItmeId}
