mapper.update:
UPDATE auction_record
topic_item_id=#{topicItemId}, record_type=#{recordType}, deposit_type=#{depositType}, user_id=#{userId}, nickname=#{nickname}, mobile=#{mobile}, bid_num=#{bidNum}, exchange_count=#{exchangeCount}, refund_num=#{refundNum}, total_currency=#{totalCurrency}, current_auct_price=#{currentAuctPrice}, deposit_id=#{depositId}, card_no=#{cardNo}, deposit_amount=#{depositAmount}, discount_id=#{discountId}, discount=#{discount},
WHERE id = #{id}

mapper.select:
SELECT
FROM auction_record
ORDER BY id DESC Limit #{start},#{pageSize}

SELECT
FROM auction_record ar WHERE ar.record_type = '01' AND ar.topic_item_id IN
#{tpItmeId}

mapper.insert:
SELECT LAST_INSERT_ID() AS id
INSERT INTO auction_record( id, topic_item_id, record_type, deposit_type, user_id, nickname, mobile, bid_num, exchange_count, refund_num, total_currency, current_auct_price, deposit_id, card_no, deposit_amount, discount_id, discount, create_time )values (
#{item.id}, #{item.topicItemId}, #{item.recordType}, #{item.depositType}, #{item.userId}, #{item.nickname}, #{item.mobile}, #{item.bidNum}, #{item.exchangeCount}, #{item.refundNum}, #{item.totalCurrency}, #{item.currentAuctPrice}, #{item.depositId}, #{item.cardNo}, #{item.depositAmount}, #{item.discountId}, #{item.discount}, #{item.createTime}
)
