mapper.update:
UPDATE exchange_order_status
record_id=#{recordId}, shipments_status=#{shipmentsStatus}, shipments_time=#{shipmentsTime}, waybill_no=#{waybillNo}, express_id=#{expressId}, remark=#{remark}, modify_user_id=#{modifyUserId},
WHERE id = #{id}

mapper.select:
SELECT
FROM exchange_order_status
ORDER BY exchange_order_status.id DESC Limit #{start},#{pageSize}

SELECT tp.id topicId, tp.topic_type topicType, tp.start_time startTime, tp.end_time endTime, ti.id tiId, ti.item_title itemTitle, ti.market_price marketPrice, ti.exchange_amount exchangeAmount, ar.mobile, ar.id recordId, ar.create_time auctTime, ar.exchange_count auctNum, eos.id exchangeOrderId, eos.shipments_status shipmentsStatus, eos.shipments_time shipmentsTime, eos.remark, eos.waybill_no waybillNo, eos.express_id expressId,ei.name companyName FROM exchange_order_status eos INNER JOIN auction_record ar ON ar.id = eos.record_id LEFT JOIN topic_item ti ON ti.id = ar.topic_item_id LEFT JOIN topic tp ON tp.id = ti.topic_id LEFT JOIN express_info ei ON ei.id = eos.express_id
AND ar.mobile = #{mobile} AND ti.item_title LIKE CONCAT('%', #{itemTitle}, '%') AND eos.shipments_status = #{shipmentsStatus}
ORDER BY eos.id DESC LIMIT #{start},#{pageSize}
